package com.syntax.class03;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int numb1=10;
		int numb2=10;
		boolean results=numb1==numb2;
		System.out.println(numb1==numb2);// checks if both variables contain the same thing.
		System.out.println(results);
		
		int numb3=10;
		int numb4=20;
		boolean results3=numb3==numb4;
		System.out.println(numb3==numb4);// checks if both variables contain the same thing.
		System.out.println(results3);   //= reasigning****** == equality
		System.out.println(numb3);
		System.out.println("*************");
		

		System.out.println(numb3>numb4);
		System.out.println(numb3<numb4);
		System.out.println("*************");
		
		System.out.println(numb3>=numb4);
		System.out.println(numb3<=numb4);
		System.out.println("*************");
		
		System.out.println(numb3!=numb4); //!= not equal to 
		
		
		
		
		
		
		
		
		
	}

}
