package com.syntax.class07;

public class MoreForLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * what is the output?
		 * 
		 */
		
		int sum=0;
		
		for (int i=1; i<=6; i++) {
		  
	        sum+=i;   	
	       
	        
	        System.out.println(sum);
		
		}
	
		int result=0;
		for (int i=0; i<=4; i+=2) {
			result*=i;
			System.out.println(result);
		}
		
		
		
		
	}

}
