package com.syntax.class08;

public class nestedifmultiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
	for(int i=0; i<=10; i++) {  
			
			
			for (int j=0; j<=10; j++) { 
		
		
		System.out.println(i+" x "+j+" = "+i*j);
		
	}
 System.out.println("----------");
}
}}